import streamlit as st
import pandas as pd
import random

# --- Spelersdata ---
spelers_data = [
    {"naam": "Lionel Messi", "club": "PSG", "rating": 93, "type": "Gold Rare",
     "afbeelding": "https://upload.wikimedia.org/wikipedia/commons/thumb/b/b4/Lionel-Messi-Argentina-2022-FIFA-World-Cup_%28cropped%29.jpg/250px-Lionel-Messi-Argentina-2022-FIFA-World-Cup_%28cropped%29.jpg"},
    {"naam": "Cristiano Ronaldo", "club": "Manchester United", "rating": 92, "type": "Gold Rare",
     "afbeelding": "https://ichef.bbci.co.uk/ace/standard/759/cpsprodpb/060f/live/72ce9980-418a-11f0-b93c-5166d2dcfadc.jpg"},
    {"naam": "Erling Haaland", "club": "Manchester City", "rating": 88, "type": "Gold",
     "afbeelding": "data:image/jpeg;base64,"},
    {"naam": "Kylian Mbapp√©", "club": "PSG", "rating": 91, "type": "Gold Rare",
     "afbeelding": "data:image/jpeg;base64,"},
    {"naam": "Kevin De Bruyne", "club": "Manchester City", "rating": 91, "type": "Gold Rare",
     "afbeelding": "data:image/jpeg;base64,"},
    {"naam": "Neymar Jr", "club": "PSG", "rating": 91, "type": "Gold Rare",
     "afbeelding": "data:image/jpeg;base64,"},
    {"naam": "Robert Lewandowski", "club": "Barcelona", "rating": 91, "type": "Gold Rare",
     "afbeelding": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQvI3M6gk4XcYneJDlRzT56Tj_wKyfPGFkXzg&s"},
    {"naam": "Mohamed Salah", "club": "Liverpool", "rating": 90, "type": "Gold Rare",
     "afbeelding": "data:image/jpeg;base64,"},
    {"naam": "Luka Modriƒá", "club": "Real Madrid", "rating": 87, "type": "Gold",
     "afbeelding": "data:image/jpeg;base64,"},
    {"naam": "Sergio Ramos", "club": "PSG", "rating": 86, "type": "Gold",
     "afbeelding": "data:image/jpeg;base64,"},
    # Je kan hier nog meer spelers toevoegen als je wilt
]
spelers_df = pd.DataFrame(spelers_data)

# --- Init sessie ---
if "coins" not in st.session_state:
    st.session_state.coins = 1000
if "gepackte_spelers" not in st.session_state:
    st.session_state.gepackte_spelers = {}
if "message" not in st.session_state:
    st.session_state.message = ""
if "toon_verzameling" not in st.session_state:
    st.session_state.toon_verzameling = False

# --- Titel ---
st.title("üéÆ EAFC Pack Opening Game")

# --- Sidebar: shop + minigame ---
st.sidebar.title("üõí Shop")
st.sidebar.write(f"üí∞ Coins: {st.session_state.coins}")

st.sidebar.markdown("---")
st.sidebar.subheader("üé≤ Minigame: Kop of Munt")
keuze = st.sidebar.radio("Kies Kop of Munt:", ["Kop", "Munt"])
if st.sidebar.button("Speel"):
    uitkomst = random.choice(["Kop", "Munt"])
    if keuze == uitkomst:
        winst = 200
        st.session_state.coins += winst
        st.session_state.message = f"üéâ Het was {uitkomst}! Je wint {winst} coins!"
    else:
        st.session_state.message = f"üò¢ Het was {uitkomst}. Geen winst..."
st.sidebar.write(st.session_state.message)

# --- Packs ---
pack_types = {
    "Gold Pack (500)": {"prijs": 500, "aantal": 3, "types": ["Gold", "Gold Rare"]},
    "Rare Pack (1000)": {"prijs": 1000, "aantal": 5, "types": ["Gold Rare", "Inform", "Icon"]},
}
TOTS_KANS = 100.0  # 5%

st.sidebar.markdown("---")
pack_naam = st.sidebar.selectbox("Kies een pack", list(pack_types.keys()))
if st.sidebar.button("Koop & Open Pack"):
    prijs = pack_types[pack_naam]["prijs"]
    if st.session_state.coins >= prijs:
        st.session_state.coins -= prijs
        opties = spelers_df[spelers_df["type"].isin(pack_types[pack_naam]["types"])].copy()

        max_rating = opties["rating"].max()
        opties["gewicht"] = max_rating - opties["rating"] + 1

        gekozen = opties.sample(
            n=pack_types[pack_naam]["aantal"],
            weights=opties["gewicht"],
            replace=False,
            random_state=random.randint(1, 100000),
        )

        st.subheader("üéÅ Je pack bevat:")
        for _, speler in gekozen.iterrows():
            is_tots = random.random() < TOTS_KANS
            rating = speler['rating'] + 3 if is_tots else speler['rating']
            naam = speler['naam'] + " (TOTS)" if is_tots else speler['naam']
            unieke_id = f"{naam}_{speler['club']}"  # uniek ID per speler

            # Alleen toevoegen als nog niet in verzameling
            if unieke_id not in st.session_state.gepackte_spelers:
                st.session_state.gepackte_spelers[unieke_id] = {
                    "naam": naam,
                    "club": speler["club"],
                    "rating": rating,
                    "type": speler["type"],
                    "afbeelding": speler["afbeelding"],
                    "tots": is_tots,
                }

            # Toon packresultaat
            if is_tots:
                st.markdown(f"<span style='color:blue;'>‚≠ê {rating} - {naam} ({speler['club']}) - {speler['type']}</span>", unsafe_allow_html=True)
            else:
                st.write(f"‚≠ê {rating} - {naam} ({speler['club']}) - {speler['type']}")
            if speler['afbeelding']:
                st.image(speler['afbeelding'], width=150)
    else:
        st.error("Niet genoeg coins!")

# --- Knop om verzameling te bekijken ---
st.markdown("---")
if st.button("üì¶ Bekijk je verzameling"):
    st.session_state.toon_verzameling = not st.session_state.toon_verzameling

if st.session_state.toon_verzameling:
    st.header("üìÇ Je verzameling")
    if not st.session_state.gepackte_spelers:
        st.info("Je hebt nog geen spelers gepackt.")
    else:
        for speler in st.session_state.gepackte_spelers.values():
            kleur = "blue" if speler["tots"] else "black"
            st.markdown(f"<span style='color:{kleur};'>‚≠ê {speler['rating']} - {speler['naam']} ({speler['club']}) - {speler['type']}</span>", unsafe_allow_html=True)
            if speler["afbeelding"]:
                st.image(speler["afbeelding"], width=100)
